import React from "react";
import { Link } from "react-router-dom";

export function connectToMetaMask() {
  const ethereum = window.ethereum;

  if (ethereum) {
    ethereum
      .request({ method: "eth_requestAccounts", params: [] })
      .then((accounts) => {
        console.log("MetaMask connected!");
      })
      .catch((error) => {
        console.log("Error connecting to MetaMask:", error);
      });
  } else {
    console.log("MetaMask extension not detected");
  }
}

export const Header = ({ back }) => {
  return (
    <div className="header bg-gray-800 border-b py-4 border-gray-900">
      <div className="custom-maxWidth flex justify-between">
        {back ? (
          <Link to="/" className="flex">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 48 48"
              width="24"
            >
              <path
                fill="currentColor"
                d="M28.05 36 16 23.95 28.05 11.9l2.15 2.15-9.9 9.9 9.9 9.9Z"
              />
            </svg>
            Coiner
          </Link>
        ) : (
          <Link to="/">Coiner</Link>
        )}

        <ul className="flex justify-between gap-5">
          <li className="hover:text-gray-300 hover:underline">
            <Link to="https://mattmajestic.dev" target="_blank">
              Contact the Developer
            </Link>
          </li>
          <li>
            <img
              src={process.env.PUBLIC_URL + "/Metamask_Fox.png"}
              alt=" Connect MetaMask"
              className="metamask-image"
              onClick={connectToMetaMask}
            />
          </li>
        </ul>
      </div>
    </div>
  );
};
